mapper.select:
select id, email, name, grade
from user
where id = #{id} and pass = #{pass}

select id,email, name, grade
from user
where id = #{userid}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}
